mapper.update:
update SM_USERS set
	user_phone = #{user_phone},
	user_description = #{user_description}
where
	user_id = #{user_id}

update SM_USERS set
	user_password = #{user_password},
	user_date_last_pw_changed = #{user_date_last_pw_changed}
where
	user_id = #{user_id}

update SM_USERS set
  user_date_last_login = #{user_date_last_login}
where
  user_id = #{user_id}
